import React from "react";
import { View, StyleSheet, Dimensions, Text, Image } from "react-native";

var { width } = Dimensions.get("window");

const ProductCard = ({ name, price, image }) => {
  return (
    <View style={styles.container}>
      <Image
       style={styles.img}
       resizeMode="contain"
       source={{uri: image}}
        />
      <View style={styles.card} />
      <View style={styles.title} >
          <Text>{name.length > 15 ? name.substring(0, 15 -3) + '...': name }</Text>
      </View>
      <Text style={styles.price}>${price}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    width: width / 2 - 20,
    height: width / 1.7,
    padding: 10,
    borderRadius: 10,
    marginTop: 55,
    marginBottom: 5,
    elevation: 8,
    alignItems: "center",
    backgroundColor: 'white'
  }, 
  img: {
    width: width / 2 - 20 - 10,
    height: width / 2 - 20 - 30,
    backgroundColor: 'transparent',
    position: 'absolute',
     top: -45,
  },
  card: {
      marginBottom: 10,
      height: width / 3 - 20,
      backgroundColor: 'transparent',
      width: width / 2 - 20 -10
  },
  title: {
      fontWeight: 'bold',
      fontSize: 14,
      textAlign: 'center'
  },
  price: {
      fontSize: 22,
      color: 'orange',
      marginTop: 5
  }
});

export default ProductCard;
